<?php
/**
 * The template for displaying search results pages
 *
 * @link https://developer.wordpress.org/themes/basics/template-hierarchy/#search-result
 *
 * @package Cartzilla
 */

get_header();

?>
<div class="page-title bg-dark py-4">
	<div class="container py-2 py-lg-3">
		<div class="row justify-content-center">
			<div class="col-lg-8">
				<form role="search" method="get" class="search-form input-group-overlay" action="<?php echo esc_url( home_url( '/' ) ); ?>">
					<div class="input-group-prepend-overlay">
						<span class="input-group-text"><i class="czi-search"></i></span>
					</div>
					<input type="search" name="s" class="form-control prepended-form-control" value="<?php echo get_search_query(); ?>">
				</form>
			</div>
		</div>
	</div>
</div>

<?php do_action( 'cartzilla_search_before' ); ?>

<div class="container pb-5 mb-2 mb-md-4">
	<div class="row justify-content-center pt-5">
		<section class="col-lg-8">
			<?php
			if ( have_posts() ) :

				do_action( 'cartzilla_loop_before' );

				while ( have_posts() ) :
					the_post();
					get_template_part( 'templates/contents/search', get_post_type() );
				endwhile;

				do_action( 'cartzilla_loop_after' );

			else : ?>
				<div class="text-center pt-sm-5 pb-sm-3">
					<img src="data:image/png;base64," width='70' class="d-inline-block mb-4" alt="Noting found">
					<p class="text-muted mb-0"><?php echo esc_html_x( 'Nothing found! Please refine your search and try again.', 'front-end', 'cartzilla' ); ?></p>
				</div>
			<?php endif; ?>
		</section>
	</div>
</div>
<?php

do_action( 'cartzilla_search_after' );

get_footer();
